mapper.select:
	SELECT T.*
	  FROM TD_S_PARAM T
	 WHERE 1 = 1
	   AND T.VALID_FLAG = '1'
   	   AND T.TYPE_ID = #{typeId}
	   
	   	   
		   		AND T.DATA_ID like #{dataId} || '%'
		   
		   
		   		AND T.DATA_ID = #{dataId}
		   
	   
	   
		   AND T.DATA_NAME = #{dataName}
	   
	   ORDER BY T.TYPE_ID, T.ORDER_NO

SELECT A.LOG_ID, A.EXPORT_NAME, A.EXPORT_MODE, A.EXPORT_STATE, A.EXPORT_PATH, A.REQ_URI, A.TOTAL, A.FILE_SIZE, A.OPERATE_IP, A.CREATE_STAFF, A.CREATE_TIME, A.APPOINTMENT_TIME, A.FINISH_TIME, A.RETRY_NUMS, A.ERROR_INFO, A.REMARK, CASE WHEN A.EXPORT_MODE = '1' THEN '同步' WHEN A.EXPORT_MODE = '2' THEN '异步' END EXPORT_MODE_NAME, CASE WHEN A.EXPORT_STATE = '0' THEN '导出中' WHEN A.EXPORT_STATE = '1' THEN '导出成功' WHEN A.EXPORT_STATE = '2' THEN '导出失败' WHEN A.EXPORT_STATE = '3' THEN '预约导出中' END EXPORT_STATE_NAME FROM TL_M_EXPORTLOG A
AND A.LOG_ID = #{exportId}
AND A.EXPORT_NAME LIKE '%' || #{exportName} || '%'
AND A.EXPORT_MODE = #{exportMode}
AND A.EXPORT_STATE = #{exportState}
AND A.CREATE_STAFF = #{createStaff}
AND A.CREATE_TIME >= TO_DATE(#{beginDate}, 'YYYY-MM-DD')
AND A.CREATE_TIME <= TO_DATE(#{endDate}, 'YYYY-MM-DD') + 1
ORDER BY A.CREATE_TIME DESC
